use std::ptr;

use num;

fn read_bytes<T>(data: *const u8, offset: isize, num_bits: isize) -> T
where
    T: Default + num::Unsigned + num::FromPrimitive,
{
    assert!(data != ptr::null(), "Reading uninitialized structure");
    let mut reader: *const u8 = unsafe { data.offset(offset / 8) };
    let mut bits_left = num_bits;
    let local_offset = offset % 8;
    let mut result: u64 = 0;
    // read first unaligned byte
    if local_offset != 0 {
        result = unsafe { *reader as u64 };
        result >>= local_offset;
        if bits_left <= 8 - local_offset {
            result &= (1 << bits_left) - 1;
            return T::from_u64(result).unwrap(); // extend sign
        }
        bits_left -= 8 - local_offset;
        reader = unsafe { reader.offset(1) };
    }
    while 8 <= bits_left {
        let mut temp: u64 = unsafe { *reader as u64 };
        temp <<= num_bits - bits_left;
        result |= temp;

        bits_left -= 8;
        reader = unsafe { reader.offset(1) };
    }
    // read last unaligned byte
    if bits_left != 0 {
        let mut temp: u64 = unsafe { *reader as u64 };
        temp &= (1 << bits_left) - 1;
        temp <<= num_bits - bits_left;
        result |= temp;
    }
    T::from_u64(result).unwrap() // extend sign
}

